import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Swal from "sweetalert2";
function ScheduleAndDateAdd()
{

  
const [date,setdate]=useState('');
const [day,setday]=useState('');
const [time,settime]=useState('');
const [subject,setsubject]=useState('');
const [venue,setvenue]=useState('');
const [speaker,setspeaker]=useState('');
const [eventspeaker, seteventspeaker] = useState([]);
useEffect(() => {
  axios.get('http://localhost:4000/viewevent')
      .then(response => {
          seteventspeaker(response.data.map(event => event.speaker));
      })
      .catch(error => console.error(error));
}, []);
    const Addshedule=()=>{
        if (date=="")
        {
            Swal.fire({
                title: "Validation Message",
                text: "Date Field is Required*",
                icon: "error"
              });   
        }
        else if (day=="")
        {
            Swal.fire({
                title: "Validation Message",
                text: "Day Field is Required*",
                icon: "error"
              });   
        }
        else if (time=="")
        {
            Swal.fire({
                title: "Validation Message",
                text: "Time Field is Required*",
                icon: "error"
              });   
        }
        else if (subject=="")
        {
            Swal.fire({
                title: "Validation Message",
                text: "Subject Field is Required*",
                icon: "error"
              });   
        }
        else if (venue=="")
        {
            Swal.fire({
                title: "Validation Message",
                text: "Venue Field is Required*",
                icon: "error"
              });   
        }
        else if(speaker=="")
        {
          Swal.fire({
                title: "Validation Message",
                text: "Speaker Field is Required*",
                icon: "error"
              });
        }
        else{
            axios.post('http://localhost:4000/shedule',{date,day,time,subject,venue,speaker})
            Swal.fire({
                title: "Validation Message",
                text: "Shedule Registered",
                icon: "success"
              }); 
        }
    }
        return(
            <div>



<aside class="main-sidebar sidebar-dark-primary elevation-4">

  <a href="index3.html" class="brand-link">
 
  <span class="brand-text font-weight-light text-center">Event Sphere</span>
</a>


<div class="sidebar">

  <nav class="mt-2">
    <ul class="nav nav-pills nav-sidebar flex-column" data-widget="treeview" role="menu" data-accordion="false">
     
      <li class="nav-item has-treeview menu-open">
        <Link to="/admindashboard" class="nav-link ">
          <i class="nav-icon fas fa-tachometer-alt"></i>
          <p className="text-light">
       Contact Data
          <i></i>
          </p>
        </Link>
        </li>
        <li class="nav-item has-treeview menu-open">
        <Link to="/userdata" class="nav-link ">
          <i class="nav-icon fas fa-tachometer-alt"></i>
          <p className="text-light">
        Registerations
          <i></i>
          </p>
        </Link>
       
        <Link to="/floorplan" class="nav-link ">
          <i class="nav-icon fas fa-tachometer-alt"></i>
          <p className="text-light">
          Add Floor Plan 
          <i></i>
          </p>
        </Link>
        <Link to="/adminfloorplan" class="nav-link ">
          <i class="nav-icon fas fa-tachometer-alt"></i>
          <p className="text-light">
          Fetch Floor Plan 
          <i></i>
          </p>
        </Link>
        <Link to="/vieweventreq" class="nav-link ">
          <i class="nav-icon fas fa-tachometer-alt"></i>
          <p className="text-light">
       Events Request
          <i></i>
          </p>
        </Link>
