import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';


function Signup()
{
const [name, setname] = useState('');
const [email, setemail] = useState('');
const [pass, setpass] = useState('');

const navigate = useNavigate();
const insertrecord = () =>
{
if(name == "")
{
    Swal.fire({
        title: "Validation Message",
        text: "Name Field iS Required*",
        icon: "wrong"
      });
}
else if(email == "")
{
    Swal.fire({
        title: "Validation Message",
        text: "Email Field iS Required*",
        icon: "wrong"
      });
}

else if(pass == "")
{
    Swal.fire({
        title: "Validation Message",
        text: "Password Field iS Required*",
        icon: "wrong"
      });
}
else
{
    axios.post('http://localhost:4000',{name,email,pass})
    navigate('/login');    

}
}

    return(
        <div>
            <div class="container">
            <h3 className='mt-3'> Signup Form</h3>

                <input type='text' placeholder='Name' onChange={(e)=>setname(e.target.value)} className='form-control w-50 mt-3'/>
                <input type='email' placeholder='Email' onChange={(e)=>setemail(e.target.value)} className='form-control w-50 mt-3'/>
                <input type='password' placeholder='Password ' onChange={(e)=>setpass(e.target.value)} className='form-control w-50 mt-3'/>
<select className='form-control w-50 mt-3'>
<option disabled selected>Choose Type</option>
<option>User</option>
<option>Exhi</option>
</select>
                <button onClick={insertrecord} className='btn btn-dark mt-3'>Add Record</button>
            </div>
        </div>
    )
}


export default Signup
