const axios = require('axios');
const React = {  } = require('react');
const { Link, useNavigate } = require('react-router-dom');
const Swal = require('sweetalert2');

function Signup() {
    const [name, setname] = useD('');
    const [email, setemail] = useState('');
    const [pass, setpass] = useState('');
    const [type, settype] = useState('');
    const navigate = useNavigate();

    const insertrecord = async () => {
        if (name === "") {
            Swal.fire({
                title: "Validation Message",
                text: "Name Field is Required*",
                icon: "error"
            });
        } else if (email === "" || !email.includes("@")) {
            Swal.fire({
                title: "Validation Message",
                text: "Please enter a valid email address",
                icon: "error"
            });
        } else if (!/^(?=.*[a-zA-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{3,}$/.test(pass)) {
            Swal.fire({
                title: "Validation Message",
                text: "Password must contain at least 3 letters, 3 numbers, and 1 special character",
                icon: "error"
            });
        } else if (type === "") {
            Swal.fire({
                title: "Validation Message",
                text: "Type Field is Required*",
                icon: "error"
            });
        } else {
            try {
                const res = await axios.post('http://localhost:4000', { name, email, pass, type });
                console.log(res.data);
                if (res.data.success) {
                    Swal.fire({
                        title: "Validation Message",
                        text: "Signup successful",
                        icon: "success"
                    }).then(() => {
                        setTimeout(() => {
                            navigate('/login');
                        }, 2000);
                    });
                } else {
                    Swal.fire({
                        title: "Validation Message",
                        text: "Signup failed",
                        icon: "error"
                    });
                }
            } catch (error) {
                console.error("Error:", error.message);
                Swal.fire({
                    title: "Error",
                    text: "Something went wrong. Please try again later.",
                    icon: "error"
                });
            }
        }
    };

    return (
        <div>

<nav class="navbar main-nav border-less fixed-top navbar-expand-lg p-0">
  <div class="container-fluid p-0">
      {/* logo */}
      <Link class="navbar-brand" to="/">
        <img src="images/logo.png" alt="logo"/>
      </Link>
      <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
      <span class="fa fa-bars"></span>
      </button>
      <div class="collapse navbar-collapse" id="navbarNav">
      <ul class="navbar-nav mx-auto">
        <li class="nav-item dropdown active dropdown-slide">
          <Link class="nav-link" to="/"  data-toggle="dropdown">Home
          </Link>
         
        </li>
		<li class="nav-item">
			<Link class="nav-link" to="/about">AboutUs</Link>
		  </li>
        <li class="nav-item">
          <Link class="nav-link" to="/speaker">Speakers
           
          </Link>
        </li>
      
        <li class="nav-item">
          <Link class="nav-link" to="/schedule">Schedule</Link>
        </li>
        <li class="nav-item">
			<Link class="nav-link" to="/event">Events</Link>
		  </li>
        <li class="nav-item">
			<Link class="nav-link" to="/gallery">Gallery
			</Link>
		  </li>
		  <li class="nav-item">
			<Link class="nav-link" to="/pricing">Pricing
			</Link>
		  </li>
		  <li class="nav-item">
			<Link class="nav-link" to="/faq">FAQ
			</Link>
		  </li>
        <li class="nav-item">
          <Link class="nav-link" to="/contact">Contact</Link>
        </li>
		
      </ul>
      
      </div>
  </div>
</nav>

<br/>
<br/>
<br/>
<br/>
<br/>
<br/>
            <div class="container">
            <h3 className='mt-3'> Signup Form</h3>

                <input type='text' placeholder='Name' onChange={(e)=>setname(e.target.value)} className='form-control w-50 mt-3'/>
                <input type='email' placeholder='Email' onChange={(e)=>setemail(e.target.value)} className='form-control w-50 mt-3'/>
                <input type='password' placeholder='Password ' onChange={(e)=>setpass(e.target.value)} className='form-control w-50 mt-3'/>
<select onChange={(e)=>settype(e.target.value)} className='form-control w-50 mt-3'>
<option disabled selected>Choose Type</option>
<option value="user">User</option>
