import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function EventRequest() {
    const [events, setEvents] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:4000/viewevent')
            .then(response => setEvents(response.data))
            .catch(err => console.log(err));
    }, []);

    const handleAccept = (id) => {
        axios.put(`http://localhost:4000/acceptevent/${id}`)
            .then(response => {
                console.log(response.data);
                // Update the status of the event in the local state
                const updatedEvents = events.map(event => {
                    if (event._id === id) {
                        event.status = 'Request Accepted';
                    }
                    return event;
                });
                setEvents(updatedEvents);
            })
            .catch(err => console.log(err));
    };

    const handleReject = (id) => {
        axios.put(`http://localhost:4000/rejectevent/${id}`)
            .then(response => {
                console.log(response.data);
                // Update the status of the event in the local state
                const updatedEvents = events.map(event => {
                    if (event._id === id) {
                        event.status = 'Request Rejected';
                    }
                    return event;
                });
                setEvents(updatedEvents);
            })
            .catch(err => console.log(err));
    };

    return (
            <div>
    <nav className="main-header navbar navbar-expand navbar-white navbar-light">
        <ul className="navbar-nav">
          <li className="nav-item">
            <a className="nav-link" data-widget="pushmenu" href="#" role="button"><i className="fas fa-bars"></i></a>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/exhibitordashboard" className="nav-link">My Profile</Link>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/addevent" className="nav-link">Add Events</Link>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/eventrequesttoadmin" className="nav-link">Request For Events</Link>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/exhibitoreventstatus" className="nav-link">MyEvents</Link>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/exhibitorticketstatus" className="nav-link">Tickets Status</Link>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/talkwithadmin" className="nav-link">Communicate With Admin</Link>
          </li>
        </ul>
      </nav>

      <aside className="main-sidebar sidebar-dark-primary elevation-4">
        <a href="index3.html" className="brand-link">
          <span className="brand-text font-weight-light text-center">Event Sphere</span>
        </a>
        <div className="sidebar">
          <nav className="mt-2">
            <ul className="nav nav-pills nav-sidebar flex-column" data-widget="treeview" role="menu" data-accordion="false">
              <li className="nav-item has-treeview menu-open">
                <Link to="/exhibitordashboard" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">My Profile<i className="right fas fa-angle-left"></i></p>
                </Link>
              </li>
              <li className="nav-item has-treeview menu-open">
                <Link to="/addevent" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">Add Events<i className="right fas fa-angle-left"></i></p>
                </Link>
              </li>
              <li className="nav-item has-treeview menu-open">
                <Link to="/eventrequesttoadmin" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">Request For Events<i className="right fas fa-angle-left"></i></p>
                </Link>
                <Link to="/exhibitoreventstatus" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">My Events<i className="right fas fa-angle-left"></i></p>
                </Link>
                <Link to="/exhibitorticketstatus" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">Tickets Status<i className="right fas fa-angle-left"></i></p>
                </Link>
              </li>
              <li className="nav-item has-treeview menu-open">
                <Link to="/talkwithadmin" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">Communicate With Admin<i className="right fas fa-angle-left"></i></p>
                </Link>
              </li>
            </ul>
          </nav>
        </div>
      </aside>
    {/* Work Start */}
    <div className="content-wrapper">
                <div className="content-header">
                    <div className="container-fluid">
                        <div className="card-deck">
                            {events.map((event) => (
                                <div className="card" key={event._id}>
                                    <img className="card-img-top" src={event.image} alt="Event" />
                                    <div className="card-body">
                                        <h6 className="card-text">Event Title: {event.title}</h6>
                                        <h6 className="card-text">Event Speaker: {event.speaker}</h6>
                                        <h6 className="card-text">Event Date: {event.date}</h6>
                                        <h6 className="card-text">Event Time: {event.time}</h6>
                                        <h6 className="card-text">Event Venue: {event.venue}</h6>
                                        <h6 className="card-text">Event Description: {event.description}</h6>

                                        {event.status === 'Pending' && (
                                            <div>
                                                <button type="button" className="btn btn-success" onClick={() => handleAccept(event._id)}>Accept</button>
                                                <button type="button" className="btn btn-danger" onClick={() => handleReject(event._id)}>Reject</button>
                                            </div>
                                        )}
                                        {event.status === 'Request Accepted' && (
                                            <button type="button" className="btn btn-success" disabled>Request Accepted</button>
                                        )}
                                        {event.status === 'Request Rejected' && (
                                            <button type="button" className="btn btn-danger" disabled>Request Rejected</button>
                                        )}
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default EventRequest;
