import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';


function Login()
{
const [loginemail, setloginemail] = useState('');
const [loginpass, setloginpass] = useState('');


const navigate = useNavigate();
const loginrecord = () =>
{
axios.post('http://localhost:4000/login',{loginemail,loginpass}).then(
    result => {console.log(result)
    
    
    if(result.data ==='success')
    {
        navigate('/');
    }
    
    }
    
)
   

}

    return(
        <div>
            <div class="container mt-5">
                <h3> Login Form</h3>
                <input type='email' placeholder='Email' onChange={(e)=>setloginemail(e.target.value)} className='form-control w-50 mt-3'/>
                <input type='password' placeholder='Password ' onChange={(e)=>setloginpass(e.target.value)} className='form-control w-50 mt-3'/>

                <button onClick={loginrecord} className='btn btn-dark mt-3 mb-3'>Login Here</button>
            </div>
        </div>
    )
}


export default Login
