import axios from "axios";
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Swal from "sweetalert2";

function AddEvent() {
  const [name, setName] = useState('');
  const [title, setTitle] = useState('');
  const [speaker, setSpeaker] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [venue, setVenue] = useState('');
  const [schedule, setSchedule] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:4000/shedulefetch')
      .then(response => setSchedule(response.data))
      .catch(error => console.error('Error fetching schedule:', error));
  }, []);

  const handleChange = (event) => {
    setImage(event.target.files[0]);
  };

  const addEvent = () => {
    if (!name || !title || !speaker || !description || !date || !time || !venue || !image) {
      Swal.fire({
        title: "Validation Message",
        text: "All fields are required",
        icon: "warning"
      });
    } else {
      const formData = new FormData();
      formData.append('name', name);
      formData.append('title', title);
      formData.append('speaker', speaker);
      formData.append('description', description);
      formData.append('date', date);
      formData.append('time', time);
      formData.append('venue', venue);

      axios.post('http://localhost:4000/addevent', formData)
        .then(response => {
          Swal.fire({
            title: "Validation Message",
            text: "Schedule Registered",
            icon: "success"
          });
        })
        .catch(error => {
          console.error('Error registering schedule:', error);
          Swal.fire({
            title: "Validation Message",
            text: "Failed to register schedule",
            icon: "error"
          });
        });
    }
  };

  return (
    <div>
      <nav className="main-header navbar navbar-expand navbar-white navbar-light">
        <ul className="navbar-nav">
          <li className="nav-item">
            <a className="nav-link" data-widget="pushmenu" href="#" role="button"><i className="fas fa-bars"></i></a>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/exhibitordashboard" className="nav-link">My Profile</Link>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/addevent" className="nav-link">Add Events</Link>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/eventrequesttoadmin" className="nav-link">Request For Events</Link>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/exhibitoreventstatus" className="nav-link">MyEvents</Link>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/exhibitorticketstatus" className="nav-link">Tickets Status</Link>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/talkwithadmin" className="nav-link">Communicate With Admin</Link>
          </li>
        </ul>
      </nav>

      <aside className="main-sidebar sidebar-dark-primary elevation-4">
        <a href="index3.html" className="brand-link">
          <span className="brand-text font-weight-light text-center">Event Sphere</span>
        </a>
        <div className="sidebar">
          <nav className="mt-2">
            <ul className="nav nav-pills nav-sidebar flex-column" data-widget="treeview" role="menu" data-accordion="false">
              <li className="nav-item has-treeview menu-open">
                <Link to="/exhibitordashboard" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">My Profile<i className="right fas fa-angle-left"></i></p>
                </Link>
              </li>
              <li className="nav-item has-treeview menu-open">
                <Link to="/addevent" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">Add Events<i className="right fas fa-angle-left"></i></p>
                </Link>
              </li>
              <li className="nav-item has-treeview menu-open">
                <Link to="/eventrequesttoadmin" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">Request For Events<i className="right fas fa-angle-left"></i></p>
                </Link>
                <Link to="/exhibitoreventstatus" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">My Events<i className="right fas fa-angle-left"></i></p>
                </Link>
                <Link to="/exhibitorticketstatus" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">Tickets Status<i className="right fas fa-angle-left"></i></p>
                </Link>
              </li>
              <li className="nav-item has-treeview menu-open">
                <Link to="/talkwithadmin" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">Communicate With Admin<i className="right fas fa-angle-left"></i></p>
                </Link>
              </li>
            </ul>
          </nav>
        </div>
      </aside>

      <div className="content-wrapper">
        <div className="content-header">
          <div className="container-fluid">
            <form id="addeventform">
              <h1>Add Event</h1>
              <br />
              <div className="form-outline mb-4">
                <input type="text" value={name} onChange={(e) => setName(e.target.value)} className="form-control" placeholder="Exhibitor Name" />
              </div>
              <div className="form-outline mb-4">
                <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} className="form-control" placeholder="Event Title" />
              </div>
              <div className="form-outline mb-4">
                <input type="text" value={speaker} onChange={(e) => setSpeaker(e.target.value)} className="form-control" placeholder="Event Speaker" />
              </div>
              <div className="form-outline mb-4">
                <textarea className="form-control" value={description} onChange={(e) => setDescription(e.target.value)} rows="4" placeholder="Event Description"></textarea>
              </div>
              <div className="form-outline mb-4">
                <label className="form-label" htmlFor="customFile">Select Date That Best Suits For The Events</label>
                <select className="form-control" value={date} onChange={(e) => setDate(e.target.value)} aria-label="Default select example">
                  <option disabled>Select Date</option>
                  {schedule.map(event => (
                    <option key={event.date} value={event.date}>{event.date}</option>
                  ))}
                </select>
              </div>
              <div className="form-outline mb-4">
                <label className="form-label" htmlFor="customFile">Select Time That Best Suits For The Events</label>
                <select className="form-control" value={time} onChange={(e) => setTime(e.target.value)} aria-label="Default select example">
                  <option disabled>Select Time</option>
                  {schedule.map(event => (
                    <option key={event.time} value={event.time}>{event.time}</option>
                  ))}
                </select>
              </div>
              <div className="form-outline mb-4">
                <label className="form-label" htmlFor="customFile">Select Venue That Best Suits For The Events</label>
                <select className="form-control" value={venue} onChange={(e) => setVenue(e.target.value)} aria-label="Default select example">
                  <option disabled>Select Venue</option>
                  {schedule.map(event => (
                    <option key={event.venue} value={event.venue}>{event.venue}</option>
                  ))}
                </select>
              </div>
          
              <div className="form-outline mb-4">
                <button type="button" onClick={addEvent} className="btn btn-primary">Add Event</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AddEvent;
