import axios from "axios";
import React, { useState, useEffect} from "react";
import { Link } from "react-router-dom";
import Swal from "sweetalert2";

function AddEvent() {
const [name, setname]=useState('');
const [title, settitle]=useState('');
const [speaker, setspeaker]=useState('');
const [description, setdescription]=useState('');
const [date, setdate]=useState('');
const [time, settime]=useState('');
const [venue, setvenue]=useState('');

const [scheduleDates, setScheduleDates] = useState([]);
const [scheduletime, setScheduletime] = useState([]);
const [schedulevenue, setScheduleevent] = useState([]);


useEffect(() => {
  axios.get('http://localhost:4000/shedulefetch')
      .then(response => {
          setScheduleDates(response.data.map(schedule => schedule.date));
          setScheduletime(response.data.map(schedule => schedule.time));
          setScheduleevent(response.data.map(schedule => schedule.time));

      })
      .catch(error => console.error(error));
}, []);

const addevent=()=>{
  if (name === "")
  {
    Swal.fire({
			title: "Validation Message",
			text: "Name Field iS Required*",
			icon: "error"
		  });
  }
  else if (title === "")
  {
    Swal.fire({
			title: "Validation Message",
			text: "Title Field iS Required*",
			icon: "error"
		  });
  }
  else  if (speaker === "")
  {
    Swal.fire({
			title: "Validation Message",
			text: "speaker Field iS Required*",
			icon: "error"
		  });
  }
  else if (description === "")
  {
    Swal.fire({
			title: "Validation Message",
			text: "Description Field iS Required*",
			icon: "error"
		  });
  }
  else if (date === "")
  {
    Swal.fire({
			title: "Validation Message",
			text: "Date Field iS Required*",
			icon: "error"
		  });
  }
  else{
    axios.post('http://localhost:4000/addevent',{name,title,speaker,description,date,time})
    Swal.fire({
			title: "Validation Message",
			text: "form done",
			icon: "success"
		  });
  }
}
  return (
    <div>
      <nav className="main-header navbar navbar-expand navbar-white navbar-light">
        <ul className="navbar-nav">
          <li className="nav-item">
            <a className="nav-link" data-widget="pushmenu" href="#" role="button"><i className="fas fa-bars"></i></a>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/exhibitordashboard" className="nav-link">My Profile</Link>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/addevent" className="nav-link">Add Events</Link>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/eventrequesttoadmin" className="nav-link">Request For Events</Link>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/exhibitoreventstatus" className="nav-link">MyEvents</Link>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/exhibitorticketstatus" className="nav-link">Tickets Status</Link>
          </li>
          <li className="nav-item d-none d-sm-inline-block">
            <Link to="/talkwithadmin" className="nav-link">Communicate With Admin</Link>
          </li>
        </ul>
      </nav>

      <aside className="main-sidebar sidebar-dark-primary elevation-4">
        <a href="index3.html" className="brand-link">
          <span className="brand-text font-weight-light text-center">Event Sphere</span>
        </a>
        <div className="sidebar">
          <nav className="mt-2">
            <ul className="nav nav-pills nav-sidebar flex-column" data-widget="treeview" role="menu" data-accordion="false">
              <li className="nav-item has-treeview menu-open">
                <Link to="/exhibitordashboard" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">My Profile<i className="right fas fa-angle-left"></i></p>
                </Link>
              </li>
              <li className="nav-item has-treeview menu-open">
                <Link to="/addevent" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">Add Events<i className="right fas fa-angle-left"></i></p>
                </Link>
              </li>
              <li className="nav-item has-treeview menu-open">
                <Link to="/eventrequesttoadmin" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">Request For Events<i className="right fas fa-angle-left"></i></p>
                </Link>
                <Link to="/exhibitoreventstatus" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">My Events<i className="right fas fa-angle-left"></i></p>
                </Link>
                <Link to="/exhibitorticketstatus" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">Tickets Status<i className="right fas fa-angle-left"></i></p>
                </Link>
              </li>
              <li className="nav-item has-treeview menu-open">
                <Link to="/talkwithadmin" className="nav-link ">
                  <i className="nav-icon fas fa-tachometer-alt"></i>
                  <p className="text-light">Communicate With Admin<i className="right fas fa-angle-left"></i></p>
                </Link>
              </li>
            </ul>
          </nav>
        </div>
      </aside>

      <div className="content-wrapper">
        <div className="content-header">
          <div className="container-fluid">
            <form id="addeventform">
              <h1>Add Event</h1>
              <br />
              <div className="form-outline mb-4">
                <input type="text" className="form-control" value={name}  onChange={(e)=>setname(e.target.value)} placeholder="Exhibitor Name" />
              </div>
              <div className="form-outline mb-4">
                <input type="text" className="form-control" value={title}  onChange={(e)=>settitle(e.target.value)} placeholder="Event Title" />
              </div>
              <div className="form-outline mb-4">
                <input type="text" className="form-control" value={speaker}  onChange={(e)=>setspeaker(e.target.value)} placeholder="Event Speaker" />
              </div>
              <div className="form-outline mb-4">
                <input type="text" className="form-control" value={description}  onChange={(e)=>setdescription(e.target.value)} placeholder="Event Description" />
              </div>
              <div className="form-outline mb-4">
                <label className="form-label" htmlFor="customFile">Select Date That Best Suits For The Events</label>
                <select className="form-control" value={date} onChange={(e) => setdate(e.target.value)}>
                  <option disabled>Select Date</option>
                  {scheduleDates.map(scheduleDate => (
                        <option key={scheduleDate} value={scheduleDate}>{scheduleDate}</option>
                    ))}
                </select>
              </div>
              <div className="form-outline mb-4">
                <label className="form-label" htmlFor="customFile">Select Date That Best Suits For The Events</label>
                <select className="form-control" value={date} onChange={(e) => settime(e.target.value)}>
                  <option disabled>Select Date</option>
                  {scheduletime.map(scheduleTime => (
                        <option key={scheduleTime} value={scheduleTime}>{scheduleTime}</option>
                    ))}
                </select>
              </div>
              <div className="form-outline mb-4">
                <button type="button" onClick={addevent} className="btn btn-primary">Add Event</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AddEvent;
