import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Swal from "sweetalert2";
function AddFloorPlan(){

    const [venue, setvenue]=useState('');
    const [number, setnumber]=useState('');
    const [Capacity, setcapacity]=useState('');
    const [floorimage, setfloorimage]=useState('');



    const [schedulevenue, setSchedulevenue] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:4000/shedulefetch')
            .then(response => {
                setSchedulevenue(response.data.map(schedule => schedule.venue));
      
            })
            .catch(error => console.error(error));
      }, []);
      
      const addfloorplan=async () => {
        if (number === "")
  {
    Swal.fire({
			title: "Validation Message",
			text: "Number Field Is Required*",
			icon: "error"
		  });
  }
  else if (Capacity === "")
  {
    Swal.fire({
            title: "Validation Message",
            text: "Capacity Field Is Required*",
            icon: "error"
    });
  }
  else if (venue === "")
  {
    Swal.fire({
			title: "Validation Message",
			text: "Venue Field Is Required*",
			icon: "error"
		  });
  }

  else{
    //   axios.post('http://localhost:4000/floorplan',{number,Capacity,venue})
    //  Swal.fire({
	// 	 	title: "Validation Message",
	// 	 	text: "Floor Plan Added",
	// 	 	icon: "success"
	// 	   });
  const formdata = new FormData();
  formdata.append("number",number);
  formdata.append("Capacity",Capacity);
  formdata.append("venue",venue);
  formdata.append("image",floorimage);
  try {
    await axios.post('http://localhost:4000/addevent', formData, {
      headers: { "Content-Type": "multipart/form-data" },
    });
    // Show success message
    Swal.fire({
      title: "Success!",
      text: "Event request has been sent to admin. Thank you for choosing us.",
      icon: "success",
      onClose: () => {
        // Reset form fields
        setname('');
        settitle('');
        setspeaker('');
        setdescription('');
        setdate('');
        settime('');
        setvenue('');
        setimage('');
      }
    });
  } catch (error) {
    console.error(error);
    Swal.fire({
      title: "Error",
      text: "Failed to add event. Please try again later.",
      icon: "error"
    });
  }










   
    const result=await
     axios.post('http://localhost:4000/floorplan',formdata,{
      
    })
  }
      }

    return(
        <div>




<aside class="main-sidebar sidebar-dark-primary elevation-4">

  <a href="index3.html" class="brand-link">
 
  <span class="brand-text font-weight-light text-center">Event Sphere</span>
</a>


<div class="sidebar">

  <nav class="mt-2">
    <ul class="nav nav-pills nav-sidebar flex-column" data-widget="treeview" role="menu" data-accordion="false">
     
      <li class="nav-item has-treeview menu-open">
        <Link to="/admindashboard" class="nav-link ">
          <i class="nav-icon fas fa-tachometer-alt"></i>
          <p className="text-light">
       Contact Data
            <i class="right fas fa-angle-left"></i>
          </p>
        </Link>
        </li>
        <li class="nav-item has-treeview menu-open">
        <Link to="/userdata" class="nav-link ">
          <i class="nav-icon fas fa-tachometer-alt"></i>
          <p className="text-light">
        Registerations
            <i class="right fas fa-angle-left"></i>
          </p>
        </Link>
       
        <Link to="/floorplan" class="nav-link ">
          <i class="nav-icon fas fa-tachometer-alt"></i>
          <p className="text-light">
       Floor Plan
            <i class="right fas fa-angle-left"></i>
          </p>
        </Link>
        <Link to="/vieweventreq" class="nav-link ">
          <i class="nav-icon fas fa-tachometer-alt"></i>
          <p className="text-light">
       Events Request
            <i class="right fas fa-angle-left"></i>
          </p>
        </Link>
        <Link to="/viewevent" class="nav-link">
          <i class="nav-icon fas fa-tachometer-alt"></i>
          <p className="text-light">
       Events Detail
            <i class="right fas fa-angle-left"></i>
          </p>
        </Link>
        <Link to="/sheduledateadd" class="nav-link ">
          <i class="nav-icon fas fa-tachometer-alt"></i>
          <p className="text-light">
       Schedule Add
            <i class="right fas fa-angle-left"></i>
          </p>
        </Link>
        <Link to="/sheduledatefetch" class="nav-link ">
          <i class="nav-icon fas fa-tachometer-alt"></i>
          <p className="text-light">
       Schedule Fetch
            <i class="right fas fa-angle-left"></i>
          </p>
        </Link>
        <Link to="/userdata" class="nav-link ">
          <i class="nav-icon fas fa-tachometer-alt"></i>
          <p className="text-light">
       Exhibitor Communication
            <i class="right fas fa-angle-left"></i>
          </p>
        </Link>
        <Link to="/userdata" class="nav-link ">
          <i class="nav-icon fas fa-tachometer-alt"></i>
          <p className="text-light">
       Tickets
            <i class="right fas fa-angle-left"></i>
          </p>
        </Link>
        </li>
        <li class="nav-item has-treeview menu-open">
       

        </li>
</ul>
        </nav>
   

</div>

</aside>

<div class="content-wrapper">
  
  <div class="content-header">


    <div class="container-fluid">
    <form id="addeventform">
        <h1>Add Schedule</h1>
