import axios from 'axios';
import React, { useState } from 'react';
import Swal from 'sweetalert2';


function Signup()
{
const [name, setname] = useState('');
const [email, setemail] = useState('');
const [pass, setpass] = useState('');
const insertrecord = () =>
{
if(name == "")
{
    Swal.fire({
        title: "Validation Message",
        text: "Name Field iS Required*",
        icon: "wrong"
      });
}
else if(email == "")
{
    Swal.fire({
        title: "Validation Message",
        text: "Email Field iS Required*",
        icon: "wrong"
      });
}

 //axios.post('http://localhost:4000',{name,email,pass});    
}

    return(
        <div>
            <div class="container">
                <input type='text' placeholder='Name' onChange={(e)=>setname(e.target.value)} className='form-control w-50 mt-3'/>
                <input type='email' placeholder='Email' onChange={(e)=>setemail(e.target.value)} className='form-control w-50 mt-3'/>
                <input type='password' placeholder='Password ' onChange={(e)=>setpass(e.target.value)} className='form-control w-50 mt-3'/>

                <button onClick={insertrecord} className='btn btn-dark mt-3'>Add Record</button>
            </div>
        </div>
    )
}


export default Signup
